import React, {useContext,Component} from 'react';
//import './App.css';
import {Route, Switch} from 'react-router-dom'

import Navbar from './components/views/Navbar'
import Home from './components/Home'
import Rooms from './components/Rooms'
import SingleRoom from './components/SingleRoom'
import ErrorPage from './components/ErrorPage'

import {RoomsContext} from "./contextStore/RoomsContext";
import {ResortContext} from "./contextStore/ResortContext";

import contextConsumer from './contextStore/combineContextConsumer-forStatefullComp'

const App=()=>{

    const contextRooms=useContext(RoomsContext);
    const contextResort=useContext(ResortContext);

    //console.log(contextRooms.name, contextResort.name)
    //static contextType={...RoomsContext,...ResortContext};
    //static contextType=RoomsContext;

    console.log(this.props)
    return (
        <React.Fragment>
            <Navbar/>
            <Switch>
                <Route path="/" exact component={Home}/>
                <Route path="/single-room/:slug" exact component={SingleRoom}/>
                <Route path="/rooms" exact component={Rooms}/>
                <Route component={ErrorPage}/>
            </Switch>
        </React.Fragment>
    );
}

export default contextConsumer()(App);
